while ($rows = mysqli_fetch_assoc($result)) {
  $jobapply++;
}

$sql = "SELECT * FROM Notification where userid = '".$id."';";
$result = mysqli_query($conn, $sql);
$Notification = 0;
while ($rows = mysqli_fetch_assoc($result)) {
  $Notification++;
}
